mapper.select:
select count(email)
from mem 
where email = #{email}

select count(id)
from mem 
where id = #{id}

select id,pwd
from mem
where id = #{id}

select count(email) 
from mem
where email = #{email}

select id
from mem
where email = #{email}

select count(id)
from mem
where email = #{email} and id =#{id}

select pwd
from mem
where email = #{email} and id =#{id}
